CREATE TABLE article (
	id SERIAL PRIMARY KEY,
	title TEXT,
	category TEXT,
	content TEXT,
	date TEXT,
	img TEXT,
	visibility TEXT
);



INSERT INTO article (title, category, content, date, img, visibility) VALUES("Mises à Jour : La guerre des dieux", "Mise à jour", "La nouvelle grosse mise à jour est sortie, retrouvez-vous au coeur de la guerre des dieux, un affrontement sans précédent entre les deux plus grandes puissances militaires de toute la galaxie. Cette mise à jour apporte un lot d'armes militaire qui pourront vous servir pour vous défendre contre les créatures hostiles et les chasseurs. Les équipes de plus de 100 personnes auront accès au plus grand vaisseau ayant jamais exister, le 'croiseur antique', qui est composé de 600 canons à Protons.", "25/06/2019", "../image/news/news1.png", "private");
INSERT INTO article (title, category, content, date, img, visibility) VALUES("Réparation des bugs sur la génération de terrain", "Fixe de bugs", "Dans cette petite mise à jour, nous avons fixé quelques problèmes de génération de terrain sur la planète principale, cela causait des crashs chez certains joueurs et les empêchaient d'avancer.", "25/06/2019", "../image/news/news3.png", "private");
INSERT INTO article (title, category, content, date, img, visibility) VALUES("Nouvelle Station spatial disponible", "Ajout", "Dans cette petite mise à jour, nous avons ajouter une station spatial dans la région reculée, elle permettra de faire des échanges plus rapidement","25/06/2019", "../image/news/news2.png", "private");
INSERT INTO article (title, category, content, date, img, visibility) VALUES("Nouvelle région disponible dans l'univers", "Ajout", "Dans cette mise à jour, une nouvelle région est disponible, elle contient de nombreuses ressources encore inutilisées.","25/06/2019", "../image/news/news2.png", "private");
INSERT INTO article (title, category, content, date, img, visibility) VALUES("Nouvelles armes disponibles", "Ajout", "Dans cette mise à jour, 3 nouvelles armes sont disponible.","25/06/2019", "../image/news/news3.png", "private");
